## Configuration dictionarys to build a Background Eclipsing# Binary (BEB).## Dictionary with parameters of the foreground starforeground = {'dist': [10.0, 0, 'PowerLaw', 2.0, 0.0, 5000.0, 0.0, ''],              'teff': [5827.17, 1, 'Normal', 5770, 100, 0.0, 0.0, ''],              'vsini': [2, 0, 'Normal', 4.0, 1.5, 0.0, 0.0, ''],              'v0': [0, 0, 'Uniform', 0, 0, 0.0, 0.0, ''],              'logg': [4.55, 1, 'Normal', 4.44, 0.1, 0.0, 0.0, ''],              'z': [0.06, 1, 'Normal', 0.0, 0.1, 0.0, 0.0, ''],             'ebmv': [0, 0, 'Uniform', 0.0, 0.0, 0.0, 0.0, ''],              }# Orbital parameters of the binary + primary and secondarybinary = {# Oribtal period          'P': [3.0, 0, 'Normal', 9.7566, 0.0012, 0.0, 0.0, ''],          # Time of primary eclipse          'T0': [0.0, 0, 'Normal', 2454598.4169, 0.00537, 0.0, 0.0, ''],           # Impact parameter b          'b': [0.85, 1, 'Uniform', 0.0, 1.0, 0.0, 0.0, ''],           # Orbital eccentricity          'ecc': [0.0, 0, 'Uniform', 0.0, 1.0, 0.0, 0.0, ''],           # Argument of pericenter          'omega': [0.0, 0, 'Uniform', 0.0, 360.0, 0.0, 0.0, ''],          # Primary          'star1': 'Blend1',           # Secondary          'star2': 'Blend2',           }# Dictionary with parameters of the primaryprimary = {# Main parameters: mass, metallicity and age           'minit': [0.16, 1, 'DoublePowerLaw', -1.55, -2.7, 1.0, 0.1, 30],           'z': [-0.86, 1, 'Uniform', -2.5, 0.5, 0.0, 0.0, ''],            'logage': [9.21, 1, 'Uniform', 8.0, 10.0, 0.0, 0.0, ''],                       # Albedo           'albedo': [0.72, 1, 'Uniform', 0.6, 1.0, 0.0, 0.0, ''],            # Distance in pc           'dist': [4016.97, 1, 'PowerLaw', 2.0, 10.0, 8000.0, 0.0, ''],            # Redenning           'ebmv': [0, 0, 'Uniform', 0.0, 0.0, 0.0, 0.0, ''],            # Projected rotational velocity in km/s           'vsini': [2, 0, 'Uniform', 0.0, 30.0, 0.0, 0.0, ''],            # Center of mass radial velocity           'v0': [10, 0, 'Uniform', -100.0, 100.0, 0.0, 0.0, ''],            } # Dictionary with parameters of the secondary star. Missing# parameters are inherited from primary.secondary = {'minit': [0.14, 1, 'DoublePowerLaw', -1.55, -2.7, 1.0, 0.1, 30],             'vsini': [2, 0, 'Uniform', 0.0, 30.0, 0.0, 0.0, ''],              'albedo': [0.9, 1, 'Uniform', 0.6, 1.0, 0.0, 0.0, ''],             }# Final dictionary to feed to ObjectBuilderinput_dict = {'IsoBinary1': binary,               'Target1': foreground,              'Blend1' : primary,              'Blend2' : secondary}  